import logo from './logo.svg';
import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      todoList:[],
      activeItem:{
        id:null,
        title:'',
        completed:false,
      },
    editing:false,
  }
  this.fetchTask = this.fetchTask.bind(this);
  };
  componentDidMount(){
    this.fetchTask();
  }
  fetchTask(){
    // console.log("featching.....");
    fetch('http://127.0.0.1:8000/api/task-list/')
    .then(response=>response.json()).then(data=>
      this.setState({
        todoList:data,
      }))
  }
  addTask(e){
    e.preventDefault();
    let div = document.getElementById('t-list');
    console.log(div);

  }
  handelChange(e){

  }
  render(){
    let tasks = this.state.todoList;
    return(
      <div className="container">
      <div id="task-container">
        <div id="form-wrapper">
          <h2>Todo</h2>
              <div style={{flex:1,marginTop:"-40px"}}>
                <button type="button" id="submit" onClick={addTask} className="btn btn-secondary"><i className="fas fa-plus-square"></i></button>
            </div>
          <div id="list-wrapper">
          {
            tasks.map(function(task,index){
              return(
                <div key={index} id="t-list" className="task-wrapper flex-wrapper">
                  <i className="far fa-circle"></i>&nbsp;<span style={{fontSize:'22px'}}>{task.title}</span>
                </div>
              )
            })
          }
        </div>
        </div>
      </div>
      </div>
    )
  }
}

export default App;
