import logo from './logo.svg';
import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      todoList:[],
      activeItem:{
        id:null,
        title:'',
        completed:false,
      },
    editing:false,
  }
  this.fetchTask = this.fetchTask.bind(this);
  this.handelChange = this.handelChange.bind(this)
  };
  componentDidMount(){
    this.fetchTask();
  }
  fetchTask(){
    // console.log("featching.....");
    fetch('http://127.0.0.1:8000/api/task-list/')
    .then(response=>response.json()).then(data=>
      this.setState({
        todoList:data,
      }))
  }
  addTask(e){
    e.preventDefault();
    let getDiv = document.getElementById('list-wrapper');
    let setDiv = document.createElement('div');
    setDiv.setAttribute('class','task-wrapper flex-wrapper')
    let inputBox = document.createElement('input');
    inputBox.setAttribute('type','text');
    inputBox.setAttribute('id','task-input');
    inputBox.setAttribute('class','task-input flex-wrapper');
    // inputBox.setAttribute('onkeypress','handelChange()')
    setDiv.appendChild(inputBox)
    getDiv.lastChild.appendChild(setDiv);
    inputBox.focus();
    document.getElementById('task-input').addEventListener('keypress',this.handelChange,false);
  }
  handelChange(e){
    console.log(e);
  }
  render(){
    let tasks = this.state.todoList;
    return(
      <div className="container">
      <div id="task-container">
        <div id="form-wrapper">
          <h2>Todo</h2>
              <div style={{flex:1,marginTop:"-40px"}}>
                <button type="button" id="submit" onClick={this.addTask} className="btn btn-secondary"><i className="fas fa-plus-square"></i></button>
            </div>
          <div id="list-wrapper">
          {
            tasks.map(function(task,index){
              return(
                <div key ={index} className="row">
                  <div className="col-sm-1">
                  <i className="far fa-circle"></i>&nbsp;
                  </div>
                  <div className="col-sm-11 task-wrapper flex-wrapper">
                    <p style={{fontSize:'20px'}}>{task.title}</p>
                  </div>
                </div>
              )
            })
          }
        </div>
        </div>
      </div>
      </div>
    )
  }
}

export default App;
