import logo from './logo.svg';
import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      todoList:[],
      activeItem:{
        id:null,
        title:'',
        completed:false,
      },
    editing:false,
  }
  this.fetchTask = this.fetchTask.bind(this);
  this.handelChange = this.handelChange.bind(this);
  this.addTask = this.addTask.bind(this);
  this.getCsrf = this.getCsrf.bind(this);
  };
  componentDidMount(){
    this.fetchTask();
  }
  fetchTask(){
    // console.log("featching.....");
    fetch('http://127.0.0.1:8000/api/task-list/')
    .then(response=>response.json()).then(data=>
      this.setState({
        todoList:data,
      }))
  }

  //get csrf token
  getCsrf(name) {
    let cookieValue = null;
    if (document.cookie && document.cookie !== '') {
        const cookies = document.cookie.split(';');
        for (let i = 0; i < cookies.length; i++) {
            const cookie = cookies[i].trim();
            if (cookie.substring(0, name.length + 1) === (name + '=')) {
                cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
                break;
            }
        }
    }
    return cookieValue;
  };

  // create a new task
  addTask(e){
    e.preventDefault();
    let getDiv = document.getElementById('list-wrapper');
    let setDiv = document.createElement('div');
    let nwNode = document.createTextNode("<div class='row'><div class='col-sm-1'><i class='far fa-circle'></i>&nbsp;</div></div>");
    // setDiv.setAttribute('class','task-wrapper flex-wrapper');
    // setDiv.innerHTML("<div class='row'><div class='col-sm-1'><i class='far fa-circle'></i>&nbsp;</div></div>");
    // let inputBox = document.createElement('input');
    // inputBox.setAttribute('type','text');
    // inputBox.setAttribute('class','task-input flex-wrapper');
    // setDiv.appendChild(inputBox)
    // getDiv.lastChild.appendChild(setDiv);
    // inputBox.focus();
    // inputBox.addEventListener('keypress',this.handelChange,false);
  }
  handelChange(e){
    if(e.keyCode===13){
      let csrf = this.getCsrf('csrftoken');
      fetch('http://127.0.0.1:8000/api/task-create/',
      {
        method:'post',
        headers:{
          'Content-type':'application/json',
          'X-CSRFToken':csrf,
        },
        body:JSON.stringify(e.target.value)
      }).then((response => {
        this.fetchTask();
        this.setState({
          activeItem:{
            id:null,
            title:'',
            completed:false,
          }
        })
        this.addTask(e);
      }).catch(function (error){
        console.log("ERROR:",error);
      })
      )}
    else{
      console.log(e.target.value);
    }
  };
  render(){
    let tasks = this.state.todoList;
    return(
      <div className="container">
      <div id="task-container">
        <div id="form-wrapper">
          <div className="row">
            <div className="col-sm-6">
              <h2>Todo</h2>
            </div>
            <div className="col-sm-6">
              <button type="button" id="submit" onClick={this.addTask} className="btn btn-secondary"><i className="fas fa-plus-square"></i></button>
            </div>
          </div>
          <div id="list-wrapper">
          {
            tasks.map(function(task,index){
              return(
                <div key ={index} className="row">
                  <div className="col-sm-1">
                  <i className="far fa-circle"></i>&nbsp;
                  </div>
                  <div className="col-sm-11 task-wrapper flex-wrapper">
                    <p style={{fontSize:'20px'}}>{task.title}</p>
                  </div>
                </div>
              )
            })
          }
        </div>
        </div>
      </div>
      </div>
    )
  }
}

export default App;
