import logo from './logo.svg';
import React from 'react';
import './App.css';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      todoList:[],
      activeItem:{
        id:null,
        title:'',
        completed:false,
      },
    editing:false,
  }
  this.fetchTask = this.fetchTask.bind(this);
  };
  componentDidMount(){
    this.fetchTask();
  }
  fetchTask(){
    // console.log("featching.....");
    fetch('http://127.0.0.1:8000/api/task-list/')
    .then(response=>response.json()).then(data=>
      this.setState({
        todoList:data,
      }))
  }
  render(){
    let tasks = this.state.todoList;
    return(
      <div className="container">
      <div id="task-container">
        <div id="form-wrapper">
          <h2>Todo</h2>
          <form name="task-form">
            <div className="flex-wrapper">
              <div style={{flex:6}}>
              <input type="text" className="form-control" id="title" placeholder="Add task.."></input>
              </div>
              <div style={{flex:1}}>
                <button type="submit" id="submit" className="btn btn-secondary"><i className="fas fa-plus-square"></i></button>
              </div>
            </div>
          </form>
        </div>
        <div id="list-wrapper">
          {
            tasks.map(function(task,index){
              return(
                <div key={index} className="task-wrapper flex-wrapper">
                  <span></span>
                </div>
              )
            })
          }
        </div>
      </div>
      </div>
    )
  }
}

export default App;
